INSERT INTO `db_wx_chengyu` (`id`, `title`, `pin`, `alpha`, `link`, `type`, `grade`, `label`, `mark`, `description`, `input_time`, `update_time`) VALUES
(1, '哀兵必胜', 'āi bīng bì shèng', '', 'http://baike.baidu.com/view/31948.htm', '', '', '', '', '①指因受欺侮而怀着激愤情绪为正义而战的军队一定能打胜仗。反引申为由横遭侵凌残害而悲愤满腔的国家和民族；能奋起抗战；必获胜利。②慈爱士卒而得人心者胜。', '0000-00-00 00:00:00', '2014-02-10 14:26:05'),
(2, '哀鸿遍野', 'āi hóng biàn yě', NULL, 'http://baike.baidu.com/view/10228.htm', NULL, '', NULL, NULL, '哀鸿：哀鸣的鸿雁。比喻啼饥号寒的灾民。 鸿雁找不到安栖的地方，没有目的地飞着，悲哀地叫着。形容流离失所的难民呻吟呼救的凄惨景象。比喻到处都是流离失所、呻吟呼号的饥民。', '0000-00-00 00:00:00', NULL),
(3, '爱不释手', 'ài bù shì shǒu', NULL, 'http://baike.baidu.com/subview/10324/5074626.htm', NULL, '', NULL, NULL, '释：放下。喜爱得舍不得放手。', '0000-00-00 00:00:00', NULL),
(4, '爱莫能助', 'ài mò néng zhù', NULL, 'http://baike.baidu.com/view/10412.htm', NULL, '', NULL, NULL, '心里愿意帮助，但限于力量或条件的限制却没有办法做到。常用于婉言推辞。', '0000-00-00 00:00:00', NULL),
(5, '爱屋及乌', 'ài wū jí wū', NULL, 'http://baike.baidu.com/view/10509.htm', NULL, '', NULL, NULL, '因为爱一处房子，也爱那房顶上的乌鸦。比喻爱一个人而连带地关爱与他有关系的人或物。说明一个人对另一个人（或事物）的关爱到了一种盲目热衷的程度。', '0000-00-00 00:00:00', NULL),
(6, '安分守己', 'ān fèn shǒu jǐ', NULL, 'http://baike.baidu.com/view/32556.htm', NULL, '', NULL, NULL, '分：本分。规矩老实，守本分，不做违法的事。也比喻保持自身的规矩。', '0000-00-00 00:00:00', NULL),
(7, '安居乐业', 'ān jū lè yè', NULL, NULL, NULL, '', NULL, NULL, '人民生活安定美满的样子，比喻安定地生活，愉快地工作。形容管理者把社会治理得很好。', '0000-00-00 00:00:00', NULL),
(8, '安贫乐道', 'ān pín lè dào', NULL, 'http://baike.baidu.com/view/32533.htm', NULL, '', NULL, NULL, '安贫：安于贫困；道：原指儒家所信奉的道德，后引申为人生的理想、信念、准则。处境虽很贫困，仍乐于坚守信仰。形容人为了自己信仰或理想的实现，宁愿处于贫困恶劣环境。', '0000-00-00 00:00:00', NULL),
(9, '安然无恙', 'ān rán wú yàng', NULL, NULL, NULL, '', NULL, NULL, '安然：平安的样子；恙：疾病，指伤害之类的产生忧伤的事。原指人平安没有疾病，现泛指事物未遭损坏或人身并无损伤。', '0000-00-00 00:00:00', NULL),
(10, '安身立命', 'ān shēn lì mìng', NULL, 'http://baike.baidu.com/view/49227.htm', NULL, '', NULL, NULL, '指生活有着落，精神有所寄托。', '0000-00-00 00:00:00', NULL),
(11, '安营扎寨', 'ān yíng zhā zhài', NULL, 'http://baike.baidu.com/view/32513.htm', NULL, '', NULL, NULL, '安、扎：建立，安置；寨：防守用的栅栏。安营：安置营房；扎寨：修筑军营栅栏。原指军队架起帐篷、修起栅栏住下。现泛指军队或其他团体建立临时驻地。', '0000-00-00 00:00:00', NULL),
(12, '按部就班', 'àn bù jiù bān', NULL, 'http://baike.baidu.com/view/29880.htm', NULL, '', NULL, NULL, '原意是写文章时篇章结构安排得体，用字造句合乎规范 。后来引申为照章办事，依次进行，不越轨，不逾格。', '0000-00-00 00:00:00', NULL),
(13, '按图索骥', 'àn tú suǒ jì', NULL, 'http://baike.baidu.com/view/32650.htm', NULL, '', NULL, NULL, '按照画像去寻求好马。比喻办事机械死板；也比喻按照线索去寻求，不懂得灵活变通。索，找；骥，良马。', '0000-00-00 00:00:00', NULL),
(14, '暗渡陈仓', 'àn dù chén cāng', NULL, 'http://baike.baidu.com/view/57357.htm', NULL, '', NULL, NULL, '暗渡陈仓，指正面迷惑敌人，而从侧翼进行突然袭击。比喻暗中进行活动。陈仓：古县名，在今陕西省宝鸡市东，为通向汉中的交通孔道。“明修栈道，暗渡陈仓”是古代战争史上的著', '0000-00-00 00:00:00', NULL),
(15, '暗箭伤人', 'àn jiàn shāng rén', NULL, 'http://baike.baidu.com/view/49322.htm', NULL, '', NULL, NULL, '基本意思为放冷箭伤害人。比喻暗地里用某种手段伤害人', '0000-00-00 00:00:00', NULL),
(16, '暗送秋波', 'àn sòng qiū bō', NULL, 'http://baike.baidu.com/view/32631.htm', NULL, '', NULL, NULL, '秋波，旧时形容美女的眼睛像秋天的水波一样清澈明亮。“暗送秋波”一词原意指暗中眉目传情，后多引申为献媚取宠。也用作“秋波暗送”、“秋波送情”。现多用于指男女间“暗恋', '0000-00-00 00:00:00', NULL),
(17, '暗无天日', 'àn wú tiān rì', NULL, 'http://baike.baidu.com/view/32628.htm', NULL, '', NULL, NULL, ' 昏暗得看不到天上的日光。形容在反动势力领导下社会的黑暗或形容没有亮光，十分幽暗。', '0000-00-00 00:00:00', NULL),
(18, '黯然失色', 'àn rán shī sè', NULL, 'http://baike.baidu.com/view/32620.htm', NULL, '', NULL, NULL, '1.心里不舒服、情绪低落的样子；失色：因惊恐而变以脸色。本指心情不好，脸色难看。后多比喻相比之下很有差距，远远不如。2.阴暗下失去原有的颜色、失去光泽。', '0000-00-00 00:00:00', NULL),
(19, '昂首阔步', 'áng shǒu kuò bù', NULL, 'http://baike.baidu.com/view/32618.htm', NULL, '', NULL, NULL, '昂：仰，高抬。仰起头，迈着大步向前。形容精神振奋，意气昂扬。', '0000-00-00 00:00:00', NULL),
(20, '嗷嗷待哺', 'áo áo dài bǔ', NULL, 'http://baike.baidu.com/view/32608.htm', NULL, '', NULL, NULL, '原意指小鸟饥饿时叫着，等待母鸟来喂食。后多形容婴儿刚出生时期待母亲哺育的样子，或比喻处境极为困难，亟待救助。', '0000-00-00 00:00:00', NULL),
(21, '八面玲珑', 'bā miàn líng lóng', NULL, 'http://baike.baidu.com/view/49574.htm', NULL, '', NULL, NULL, '原指窗户很多，四面八方通明透亮。现形容为人处世圆滑，各方面都能巧妙应对。', '0000-00-00 00:00:00', NULL),
(22, '拔苗助长', 'bá miáo zhù zhǎng', NULL, 'http://baike.baidu.com/view/32147.htm', NULL, '', NULL, NULL, '把禾苗拔起一点，来帮助它成长。比喻违反自然规律发展的客观规律，急于求成，不加思考，反而把事情弄糟。', '0000-00-00 00:00:00', NULL),
(23, '跋山涉水', 'bá shān shè shuǐ', NULL, 'http://baike.baidu.com/view/49591.htm', NULL, '', NULL, NULL, '跋山：翻过山岭；涉水，用脚蹚着水渡过大河。翻山越岭，蹚水过河。形容旅途艰苦。', '0000-00-00 00:00:00', NULL),
